package routes

import (
	"easyvpn-api/structure"
	"fmt"
	"net"
	"net/http"

	"github.com/labstack/echo/v4"
)

func GetProfile(c echo.Context) error {
	p := new(structure.Servers)

	if err := c.Bind(&p); err != nil {
		return err
	}

	if net.ParseIP(p.Ipaddress) == nil || len(p.Port) > 5 {
		return c.String(http.StatusBadRequest, "bad request")
	}

	//frontend handles the download
	return c.String(http.StatusOK, CreateProfile(p.Ipaddress, p.Port, p.Protocol))
}

